import React , {useEffect , iseState} from 'react'
import micro from '../assets/icons/m.png'
import animated from '../assets/icons/m.gif'
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import {exGetOptions,fetchData} from '../utils/fetchData'
const MicrophoneSearch = (props) => {

  const [search , setSearch] = useState('')
    const searchHandler = async ()=> {
        if(search)
        {
            const exData = await fetchData('https://exercisedb.p.rapidapi.com/exercises', exGetOptions)
            const searchedExercices = exData.filter((ex)=>
             ex.name.toLowerCase().includes(search)
             ||  ex.target.toLowerCase().includes(search)   
             ||  ex.equipment.toLowerCase().includes(search)   
             ||  ex.bodyPart.toLowerCase().includes(search)        
            )
          
           props.setExercices(searchedExercices)
        }
        
    }

    const commands = [
        {
          command: 'reset',
          callback: () => resetTranscript()
        },
        // {
        //   command: 'shut up',
        //   callback: () => setMessage('I wasn\'t talking.')
        // },
        // {
        //   command: 'Hello',
        //   callback: () => setMessage('Hi there!')
        // },
      ]
      const {
        transcript,
        interimTranscript,
        finalTranscript,
        resetTranscript,
        listening,
      } = useSpeechRecognition({ commands });
     
      useEffect(() => {
        if (finalTranscript !== '') {
          console.log('Got final result:', finalTranscript);
        }
      }, [interimTranscript, finalTranscript]);
      if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
        return null;
      }
     
      if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
        console.log('Your browser does not support speech recognition software! Try Chrome desktop, maybe?');
      }
      const listenContinuously = () => {
        SpeechRecognition.startListening({
          language: 'en-GB',
        });
      };
  return (
    <>
    {listening ? <img src={animated} onClick={SpeechRecognition.stopListening} className='microphone'/>
    : <img src={micro} onClick={listenContinuously} className='microphone'/>
  }
  <p>{transcript}</p>
  </>
  )
}

export default MicrophoneSearch
