    return (
        <Stack   alignItems='center' mt={30} justifyContent='center' width="100%">
            <Typography
                fontWeight={900}
                sx={{ fontSize: { lg: '48px', sm: '32px', xs: '24px' } }}
                color='#131429' textAlign='center'>Effective Exercices You <br /> Should Explore</Typography>
            <Box  position="relative" mb="72px" style={{width : '85%' , marginTop : '1rem'}}>
                <TextField
                    sx={{
                        label: {
                            color: props.searchError ? 'red' : '#131429',
                            fontSize: {lg :'20px' , sm : '16px' },
                        },
                        input: {
                            backgroundColor :'#fff',
                            fontWeight: '700',
                            fontSize: "24px",
                            color: '#131429',

                        },
                         width: '100%'
                    }}
                    variant="filled" label= {props.searchError ? 'How Search for Empty Exercise ?' : 'Search Exercices...'} 
                    height='70px'
                    type='text'
                    value={props.search}
                    onChange={(e)=> {props.setSearch(e.target.value.toLowerCase())}}
                    onFocus={()=>{props.setSearchError(false)}}
                />
                <Button 
                onClick={props.searchHandler}
                className="search-btn" sx={{ bgcolor: '#131429', color: '#fff', textTransform: 'none', width: { lg: '173px', xs: '80px' }, height: '100%', position: 'absolute', right: '0px', fontSize: { lg: '28px', xs: '20px' } }}>
                    Search
                </Button>
            </Box>
            <Categories setExercices={props.setExercices} data={bodyParts} bodyPart={props.bodyPart} setBodyPart={props.setBodyPart}></Categories>
        </Stack>
    )
}

export default SearchExercices
