        return (
            <ArrowCircleRightIcon onClick={()=> scrollNext()} className='right-arrow'></ArrowCircleRightIcon>
        )
    }  
  return (
    <Box sx={{ position: 'relative', width: '100%', p: '20px' }}>
       <ScrollMenu LeftArrow={LeftArrow} RightArrow={RightArrow}>
          {props.data.map((item)=>{
                return (
                   <Box key={item.id || item}
                   id={item.id || item}
                   title = {item.id || item}
                   m="0 40px"
                   >
                    <CategoryItem item={item} setBodyPart={props.setBodyPart} bodyPart={props.bodyPart}></CategoryItem>
                   </Box>
                )
          })}
       </ScrollMenu>
    </Box>
  )
}

export default Categories
