import React from 'react'
import {Stack , Typography } from '@mui/material'
import GymIcon from '../assets/icons/cat.png'

const CategoryItem = (props) => {
  return (
    <Stack  type='button' alignItems='center'   justifyContent='space-around' className='bodyPart-card'
    sx={ {
       
        padding :'.5rem' ,
        backgroundColor : props.bodyPart === props.item  ? 'rgba(199, 195, 195, 0.1)' : '#fff' ,
        width : '150px',
        height :'150px',
        cursor : 'pointer',
        borderTop : props.bodyPart === props.item && '2px solid #DB175D'   ,
        borderBottom : props.bodyPart === props.item && '2px solid #DB175D'   ,
        boxShadow : '0px 2px 5px gray' ,
        borderRadius: '50%' 
    }     
    }
    onClick={()=>{props.setBodyPart}}
    >
        <img src={GymIcon} style={{width : '48px' , height : '48px'}}/>
        <Typography  fontWeight='900' textTransform='capitalize' color='#131429'>{props.item}</Typography>
    </Stack>
  )
}

export default CategoryItem
