import React from 'react'
import {Stack , Typography } from '@mui/material'
import GymIcon from '../assets/icons/cat.png'

const CategoryItem = (props) => {

  const selectCategoryHandler = ()=> {
    props.setBodyPart(props.item)
    window.scrollTo({top: 1300 , left : 100 , behavior: 'smooth'})
    // props.setExercices((prev)=>{
    //  return prev.filter((ex)=> ex.bodyPart.toLowerCase() === props.item.toLowerCase())
    // })
    
  }
  return (
    <Stack  type='button' alignItems='center'   justifyContent='space-around' className='bodyPart-card'
    sx={ {
       
        padding :'.5rem' ,
        backgroundColor : props.bodyPart === props.item  ? 'rgb(255, 233, 240)' : '#fff' ,
        width : '130px',
        height :'130px',
        cursor : 'pointer',
        borderTop : props.bodyPart === props.item && '3.5px solid rgb(119, 199, 252)'   ,
        // borderBottom : props.bodyPart === props.item && '2px solid #DB175D'   ,
        boxShadow : '0px 2px 5px gray' ,
        borderRadius: '50%' 
    }     
    }
    onClick={selectCategoryHandler}
    >
        <img src={GymIcon} alt='category' style={{width : '48px' , height : '48px'}}/>
        <Typography  fontWeight='700' textTransform='capitalize' color='#131429'>{props.item}</Typography>
    </Stack>
  )
}

export default CategoryItem
