import React, { useEffect, useState } from 'react'
import { Stack, Box, Button, TextField, Typography } from '@mui/material'
import {exGetOptions,fetchData} from '../utils/fetchData'
import Categories from './Categories'


const SearchExercices = (props) => {

    const[search , setSearch] = useState('')
    const [searchError,setSearchError] = useState(false)
    //const [searchedExercices , setSearchedExercices] = useState([])
    const [bodyParts , setBodyParts] = useState([])

    useEffect(()=>{
        const fetchExCategories = async ()=> {
            const categoriesData = await fetchData('https://exercisedb.p.rapidapi.com/exercises/bodyPartList', exGetOptions) 
            setBodyParts(['all' , ...categoriesData])

        }
        fetchExCategories()
    },[])

    const searchHandler = async ()=> {
        if(search)
        {
            props.setIsLoad(true)
            window.scrollTo({top : 1400 , behavior : 'smooth'})
            const exData = await fetchData('https://exercisedb.p.rapidapi.com/exercises', exGetOptions)
            const searchedExercices = exData.filter((ex)=>
             ex.name.toLowerCase().includes(search)
             ||  ex.target.toLowerCase().includes(search)   
             ||  ex.equipment.toLowerCase().includes(search)   
             ||  ex.bodyPart.toLowerCase().includes(search)        
            )
            if(searchedExercices.length ===0)
            {
                props.setNoExercisesFound(true)   
            }     
           props.setExercices(searchedExercices)
           setSearch('')
           props.setSearchKeyWord(()=>{
            return search
           })
           props.setIsLoad(false)
        }
        else {
            setSearchError(true)
        }
    }
    return (
        <Stack   alignItems='center' mt={30} justifyContent='center' width="100%">
            <Typography
                fontWeight={900}
                sx={{ fontSize: { lg: '48px', sm: '32px', xs: '24px' } }}
                color='#131429' textAlign='center'>Effective Exercices You <br /> Should Explore</Typography>
            <Box  position="relative" mb="72px" style={{width : '85%' , marginTop : '1rem'}}>
                <TextField
                    sx={{
                        label: {
                            color: searchError ? 'red' : '#131429',
                            fontSize: {lg :'20px' , sm : '16px' },
                        },
                        input: {
                            backgroundColor :'#fff',
                            fontWeight: '700',
                            fontSize: "24px",
                            color: '#131429',

                        },
                         width: '100%'
                    }}
                    variant="filled" label= {searchError ? 'How Search for Empty Exercise ?' : 'Search Exercices...'} 
                    height='70px'
                    type='text'
                    value={search}
                    onChange={(e)=> {setSearch(e.target.value.toLowerCase())}}
                    onFocus={()=>{setSearchError(false)}}
                />
                <Button 
                onClick={searchHandler}
                className="search-btn" sx={{ bgcolor: '#131429', color: '#fff', textTransform: 'none', width: { lg: '173px', xs: '80px' }, height: '100%', position: 'absolute', right: '0px', fontSize: { lg: '28px', xs: '20px' } }}>
                    Search
                </Button>
            </Box>
            <Categories data={bodyParts} bodyPart={props.bodyPart} setBodyPart={props.setBodyPart}></Categories>
        </Stack>
    )
}

export default SearchExercices
