import { Box } from '@mui/material'
import React, { useContext } from 'react'
import CategoryItem from './CategoryItem'
import { ScrollMenu, VisibilityContext } from 'react-horizontal-scrolling-menu';
import ArrowCircleLeftIcon from '@mui/icons-material/ArrowCircleLeft';
import ArrowCircleRightIcon from '@mui/icons-material/ArrowCircleRight';
const Categories = (props) => {
    const {next} = useContext(VisibilityContext)
    const {previos} = useContext(VisibilityContext)
    const RightArrow =   <ArrowCircleRightIcon onClick={()=> {next()}} className='svg'></ArrowCircleRightIcon>
    const LeftArrow =   <ArrowCircleLeftIcon onClick={()=> {previos()}}  className='svg'></ArrowCircleLeftIcon>
  return (
    <Box sx={{position : 'relative'  , width:'100%' , p : '20px'}}>
       <ScrollMenu LeftArrow={LeftArrow} RightArrow={RightArrow}>
          {props.data.map((item)=>{
                return (
                   <Box key={item.id || item}
                   id={item.id || item}
                   title = {item.id || item}
                   m="0 40px"
                   >
                    <CategoryItem item={item} setBodyPart={props.setBodyPart} bodyPart={props.bodyPart}></CategoryItem>
                   </Box>
                )
          })}
       </ScrollMenu>
    </Box>
  )
}

export default Categories
